import './App.css';

function App() {
  return (
<div className="bg-blue-500">
<div className="h-screen flex items-center justify-center shadow-lg shadow-custom rounded transform scale-90">
  <div className="border border-blue-300 p-8 rounded-lg bg-blue-400 max-w-md w-full shadow-lg">
    <h1 className="text-2xl font-semibold text-white mb-6 border-gray-300 pb-4" style={{ borderBottomWidth: '2px', borderBottomStyle: 'solid' }}>Weather App</h1>
    <form className="flex flex-col items-center">
      <input type="text" placeholder="Enter your location"
        className="border border-blue-300 px-4 py-2 rounded-lg w-full h-16 focus:outline-none text-center" />
      <p className="text-gray-500 text-center text-lg m-2">or</p>
      <button type="submit"
        className="bg-blue-500 shadow-custom text-white text-lg py-2 px-4 rounded-lg w-full h-16 shadow-lg">Detect Your Location</button>
    </form>
  </div>
</div>
</div>

  );
}

export default App;
