import './App.css';

function App() {
  return (
<div class="bg-blue-500">
    <div class="h-screen flex items-center justify-center shadow-lg ">
        <div class="border border-blue-300 p-8 rounded-lg bg-blue-400 max-w-md w-full shadow-custom rounded transform scale-90 shadow-lg">
            <h1 class="text-2xl font-semibold text-white mb-6 border-b-2 border-gray-300 pb-4">Weather App</h1>
            <form class="flex flex-col items-center">
                <input type="text" placeholder="Enter your location"
                       class="border border-blue-300 px-4 py-2 rounded-lg w-full h-16 focus:outline-none text-center"/>

                <p class="text-gray-500 text-center text-lg m-2">or</p>
                <button type="submit"
                class="bg-blue-500 shadow-custom text-white text-lg py-2 px-4 rounded-lg w-full h-16 shadow-lg">
                  Detect Your Location
                  </button>
            </form>
        </div>
    </div>
</div>

  );
}

export default App;
