import React from 'react';
import { useState, useEffect } from 'react';

const WeatherApi = () => {

    const [weatherData, setWeatherData] = useState(null);

    const [city, setCity] = useState("mumbai");
    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                // Replace {API key} with your actual OpenWeatherMap API key
                const apiKey = '09b8e023b1a6da1f4251f2149fc8d14a';
                const apkey = '33673353c58ee88f3119771df82a2f85';
                const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${apkey}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch weather data');
                }
                const data = await response.json();
                setWeatherData(data);
            } catch (error) {
                console.error(error);
            }
        };
        fetchWeatherData();
    }, []);

    if (!weatherData) {
        return <div>Loading...</div>;
    }
console.log(weatherData.main);
    return (
        <div className="h-screen flex items-center justify-center shadow-lg ">
        <div className="border border-blue-300 p-8 rounded-lg bg-blue-400 max-w-md w-full shadow-custom rounded transform scale-90 shadow-lg">
        <h1 className="text-2xl font-semibold text-white mb-6 border-gray-300 pb-4" style={{ borderBottomWidth: '2px', borderBottomStyle: 'solid' }}>
            Weather App
          </h1>
          <div className="flex items-center justify-center mb-6">
          <h2 className="text-4xl font-bold text-white">{}&deg;C</h2>
          <span className="text-2xl text-white ml-2">&#8451;</span>
        </div>
        <div className="border-t border-b border-gray-300 py-4 flex items-center justify-between">
          <div className="border-l border-r border-gray-300 px-8">
            <p className="text-gray-300">Feels Like</p>
            <p className="text-2xl font-semibold">{weatherData.main.feels_like}&deg;C</p>
          </div>
          {/* <div className="border-l border-r border-gray-300 px-8"> */}
          <div className="flex-grow text-center">
            <p className="text-gray-300">Humidity</p>
            <p className="text-2xl font-semibold">{weatherData.main.humidity}%</p>
          </div>
        
        </div>
      </div>
    </div>
    );
}

export default WeatherApi;
