import React from 'react';
import { useState, useEffect } from 'react';

const WeatherApi = () => {

    const [weatherData, setWeatherData] = useState(null);

    const [city, setCity] = useState("mumbai");
    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                // Replace {API key} with your actual OpenWeatherMap API key
                const apiKey = '09b8e023b1a6da1f4251f2149fc8d14a';
                const apkey = '33673353c58ee88f3119771df82a2f85';
                const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${apkey}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch weather data');
                }
                const data = await response.json();
                setWeatherData(data);
            } catch (error) {
                console.error(error);
            }
        };
        fetchWeatherData();
    }, []);

    if (!weatherData) {
        return <div>Loading...</div>;
    }

    return (
         <div className="bg-gray-300 w-96 p-4 rounded-lg shadow-md">
      <div className="flex items-center justify-center mb-4">
        <h1 className="text-4xl font-bold">&deg;C</h1>
      </div>
      <div className="text-center mb-4">
        <h2 className="text-xl font-semibold"></h2>
      </div>
      <div className="flex justify-between">
        <div>
          <p className="text-gray-600">Feels Like</p>
          <p className="text-xl font-semibold">{feelsLike}&deg;C</p>
        </div>
        <div>
          <p className="text-gray-600">Humidity</p>
          <p className="text-xl font-semibold">{humidity}%</p>
        </div>
      </div>
    </div>
    );
}

export default WeatherApi;
